// Merges multiple JSON inputs and deduplicates by "id"

let merged = [];

// Collect all results arrays from inputs
for (const item of items) {
  if (item.json.results && Array.isArray(item.json.results)) {
    merged.push(...item.json.results);
  }
}

// Deduplicate by "id"
const seen = new Set();
const deduped = merged.filter(entry => {
  if (seen.has(entry.id)) {
    return false;
  }
  seen.add(entry.id);
  return true;
});

// Return single combined output
return [
  {
    json: {
      total: deduped.length,
      results: deduped
    }
  }
];

