/**
 * Calculate business hours elapsed between two UTC timestamps.
 * @param {number|string|Date} startUTC - Start time (UTC)
 * @param {number|string|Date} endUTC - End time (UTC)
 * @param {object} [opts]
 * @param {number} [opts.startHour=9]
 * @param {number} [opts.endHour=17]
 * @param {string} [opts.timezoneOffset='+00:00']  // optional local offset e.g. '+01:00'
 * @returns {number} total business hours elapsed
 */


function calculateBusinessHours(startDate, endDate, timezoneOffset = 0, businessHours = { start: '08:30', end: '16:00' }) {
    // Validate inputs
    if (!(startDate instanceof Date) || !(endDate instanceof Date)) {
        throw new Error('Start and end dates must be valid Date objects');
    }

    if (startDate >= endDate) {
        return 0;
    }

    // Parse business hours
    const parseTime = (timeStr) => {
        const [hours, minutes] = timeStr.split(':').map(Number);
        if (isNaN(hours) || isNaN(minutes) || hours < 0 || hours > 23 || minutes < 0 || minutes > 59) {
            throw new Error('Invalid business hours format. Use "HH:MM" format.');
        }
        return { hours, minutes };
    };

    const businessStart = parseTime(businessHours.start);
    const businessEnd = parseTime(businessHours.end);

    // Adjust dates for timezone offset (in minutes)
    const adjustForTimezone = (date) => {
        return new Date(date.getTime() + (timezoneOffset * 60 * 1000));
    };

    const adjustedStart = adjustForTimezone(startDate);
    const adjustedEnd = adjustForTimezone(endDate);

    // Helper to check if a date is a weekend
    const isWeekend = (date) => {
        const day = date.getDay();
        return day === 0 || day === 6; // 0 = Sunday, 6 = Saturday
    };

    // Helper to set time on a date
    const setTime = (date, hours, minutes) => {
        const newDate = new Date(date);
        newDate.setHours(hours, minutes, 0, 0);
        return newDate;
    };

    // Helper to get start of business day
    const getBusinessDayStart = (date) => {
        return setTime(date, businessStart.hours, businessStart.minutes);
    };

    // Helper to get end of business day
    const getBusinessDayEnd = (date) => {
        return setTime(date, businessEnd.hours, businessEnd.minutes);
    };

    // Calculate duration in milliseconds
    let totalBusinessMs = 0;
    let currentDate = new Date(adjustedStart);

    // Set current date to next business day if starting outside business hours or on weekend
    if (isWeekend(currentDate) || 
        currentDate < getBusinessDayStart(currentDate) || 
        currentDate >= getBusinessDayEnd(currentDate)) {

        currentDate = getBusinessDayStart(new Date(currentDate));
        currentDate.setDate(currentDate.getDate() + 1);

        // Skip weekends
        while (isWeekend(currentDate)) {
            currentDate.setDate(currentDate.getDate() + 1);
        }
    }

    // If current date is still after end date, return 0
    if (currentDate >= adjustedEnd) {
        return 0;
    }

    // Process each day
    while (currentDate < adjustedEnd) {
        if (isWeekend(currentDate)) {
            currentDate.setDate(currentDate.getDate() + 1);
            currentDate = getBusinessDayStart(currentDate);
            continue;
        }

        const dayStart = getBusinessDayStart(currentDate);
        const dayEnd = getBusinessDayEnd(currentDate);

        // For first day, use actual start time if it's within business hours
        const effectiveStart = currentDate.getTime() === adjustedStart.getTime() ? 
            Math.max(dayStart.getTime(), adjustedStart.getTime()) : dayStart.getTime();

        // For last day, use actual end time if it's within business hours
        const effectiveEnd = currentDate.toDateString() === adjustedEnd.toDateString() ? 
            Math.min(dayEnd.getTime(), adjustedEnd.getTime()) : dayEnd.getTime();

        // Add business hours for this day
        if (effectiveStart < effectiveEnd) {
            totalBusinessMs += effectiveEnd - effectiveStart;
        }

        // Move to next business day
        currentDate.setDate(currentDate.getDate() + 1);
        currentDate = getBusinessDayStart(currentDate);

        // Skip weekends
        while (isWeekend(currentDate)) {
            currentDate.setDate(currentDate.getDate() + 1);
        }
    }

    // Convert milliseconds to hours
    return totalBusinessMs / (1000 * 60 * 60);
}

