function getBusinessHours(startUTC, endUTC, opts = {}) {
  const startHour = opts.startHour ?? 9;

 // console.log("start hour");
  //console.log(startHour);
  const endHour = opts.endHour ?? 17;
//  console.log("end hour");
 // console.log(endHour);
  const tzOffset = opts.timezoneOffset ?? '+00:00';

  // helper: parse timestamp → local Date
  const parseToLocal = (utcValue) => {
    const d = new Date(utcValue);
    // apply timezone offset manually
    const sign = tzOffset.startsWith('-') ? -1 : 1;
    const [h, m] = tzOffset.slice(1).split(':').map(Number);
    const offsetMinutes = sign * (h * 60 + (m || 0));
    d.setMinutes(d.getMinutes() + offsetMinutes);
    return d;
  };

  let start = parseToLocal(startUTC);
 // console.log("start");
 // console.log(start);
  let end = parseToLocal(endUTC);
//  console.log("end");
  //console.log(end);

  if (end <= start) return 0;

  let totalHours = 0;
  const msInHour = 1000 * 60 * 60;

  // iterate day by day
  for (
    let d = new Date(start.getFullYear(), start.getMonth(), start.getDate());
    d <= end;
    d.setDate(d.getDate() + 1)
  ) {
    const day = d.getDay(); // 0=Sun, 6=Sat
    if (day === 0 || day === 6) continue; // skip weekends

    const dayStart = new Date(d);
    dayStart.setHours(startHour, 0, 0, 0);
    const dayEnd = new Date(d);
    dayEnd.setHours(endHour, 0, 0, 0);

    // find overlap window within this day
    const rangeStart = start > dayStart ? start : dayStart;
    const rangeEnd = end < dayEnd ? end : dayEnd;

    if (rangeEnd > rangeStart) {
//    console.log(" Each totalHours = ");
 //     console.log(totalHours);
      totalHours += (rangeEnd - rangeStart) / msInHour;
    }
  }

 // console.log("Final totalHours = ");
//  console.log(totalHours);
 // console.log(" ********* ");

  return totalHours;
}

